typedef long long LL;
LL n,m,maxn,inf=1000000000000,ans; LL v[10],t[10],like[2010],bai[10],dp[2010][7000];
inline LL MAX(LL g1,LL g2){
    return g1>=g2?g1:g2;
}
int main(){
    scanf("%lld%lld",&n,&m);
    for(int i=1;i<=n;++i)
        scanf("%lld",v+i);
    bai[1]=1; bai[2]=10; bai[3]=100; bai[4]=1000; bai[5]=10000;
    for(int i=1;i<=n;++i){
        scanf("%lld",t+i);
        maxn+=t[i]*bai[i];
    }
    for(int i=1;i<=m;++i)
        scanf("%lld",like+i);
    for(int i=1;i<=m+1;++i)
        for(int j=0;j<=maxn;++j)
            dp[i][j]=inf;//dp i,j:第i天 洗衣状态为j
    dp[1][0]=0;
    for(int i=1;i<=m;++i){
        bool fff=false;
        for(int j=0;j<=maxn;++j){
            if(dp[i][j]==inf)//不存在这种状态
                continue;
            bool f=false,ff=false;
            LL shu=0,g[10];
            for(int k=1;k<=n;++k){
                g[k]=j%bai[k+1]/bai[k];//今天的状态
                if(g[k]>t[k]){
                	f=true;
                	break;
                }
                if(!g[k])
                    ff=true;
                if(g[k])
                    shu+=(g[k]-1)*bai[k];//明天的状态
            }
            if(f)//不存在这种状态
                continue;
            if(!ff)//今天没有能穿的衣服 
                continue;
            fff=true;

            for(int k=1;k<=n;++k)
            	if(!g[k]){//今天穿这件衣服 
            		int shu2=shu%bai[k]+t[k]*bai[k]+shu/bai[k+1]*bai[k+1];

            		if(dp[i+1][shu2]==inf)
            		    dp[i+1][shu2]=dp[i][j]+like[i]*v[k];
            		else
            		    dp[i+1][shu2]=MAX(dp[i+1][shu2],dp[i][j]+like[i]*v[k]);
                }
            //printf("\n");
        }
        if(!fff){
            printf("gcd loves her clothes!");
            return 0;
        }
        //return 0;
    }
    for(int i=0;i<=maxn;++i){
        if(dp[m+1][i]==inf)
            continue;
        ans=MAX(ans,dp[m+1][i]);
    }
    printf("%lld",ans);
    return 0;
