```cpp
#include <cstdio>
using namespace std;

#define reg register

inline void chkmax(long long& a,long long b)
{
	if(a<b)
	{
		a = b;
	}
}

long long dp[2005][4505];
int dped[2005][4505];

long long ci[15];
int yi[15];
long long wi[2005];

inline int nxt(int stt,int n)
{
	reg int res = 0;
	for(reg int i=1; i<=n; ++i)
	{
		if((stt>>(3*i-3))&7)
		{
			res |= (((stt>>(3*i-3))&7)-1)<<(3*i-3);
		}
	}
	return res;
}

int main()
{
	int n,m;
	scanf("%d%d",&n,&m);
	for(int i=1; i<=n; ++i)
	{
		scanf("%lld",ci+i);
	}
	for(int i=1; i<=n; ++i)
	{
		scanf("%d",yi+i);
	}
	for(int i=1; i<=m; ++i)
	{
		scanf("%lld",wi+i);
	}

	int mstat = 1<<(n*3);

	dped[1][0] = 1;
	for(int i=1; i<=m; ++i)
	{
		for(int j=0; j<=mstat; ++j)
		{
			if(!dped[i][j])
			{
				continue;
			}

			for(int k=1; k<=n; ++k)
			{
				int thd = (j>>(3*k-3))&7;
				if(thd)
				{
					continue;
				}

				int nxtstt = nxt(j,n)|(yi[k]<<(3*k-3));

				if(!dped[i+1][nxtstt])
				{
					dped[i+1][nxtstt] = 1;
					dp[i+1][nxtstt] = dp[i][j]+wi[i]*ci[k];
				}
				else
				{
					chkmax(dp[i+1][nxtstt], dp[i][j]+wi[i]*ci[k]);
				}
			}
		}
	}

	long long ans = 0x8080808080808080LL;
	for(int i=0; i<=mstat; ++i)
	{
		if(dped[m+1][i])
		{
			chkmax(ans,dp[m+1][i]);
		}
	}

	if(ans^(0x8080808080808080LL))
	{
		printf("%lld",ans);
	}
	else
	{
		printf("gcd loves her clothes!");
	}
}
