	}
	q.push(0);//从0开始连边
	int s=0;
	while(q.size()){
		int x=q.front();q.pop();
		for(int i=0;i<k;i++){
			if(((x>>i)&1)==0){//连边
				int y=x|(1<<i);
				add(x,y);
				ru[y]++;
				if(f[y]==1) continue;
				q.push(y);
				f[y]=1;
			}
		}
	}
	q.push(0);//从0开始拓扑排序
	while(q.size()){
		int x=q.front();q.pop();
		p[x]=p[x]+v[x];
		if(v[x]==1){//这个点是有贡献的
			s++;
			t[s].id=x;
			t[s].p=p[x];
		}
		ans=max(ans,p[x]);
		if(v[x]==1){
			cnt[p[x]]++;
		}
		for(int i=head[x];i;i=nxt[i]){
			int y=to[i];
			ru[y]--;
			p[y]=max(p[y],p[x]);
			if(ru[y]==0) q.push(y);
		}
	}
	cout<<1<<endl;
	cout<<ans<<endl;
	sort(t+1,t+n+1,cmp);//按照格式输出
	for(int i=1;i<=n;i++){
		if(t[i].p!=t[i-1].p){
			printf("%d ",cnt[t[i].p]);
		}
		printf("%d ",t[i].id);
		if(t[i].p!=t[i+1].p) puts("");
	}
