void add(int x,int y,int z)
{
    l1++;
    pre1[l1]=last1[x];
    last1[x]=l1;
    other1[l1]=y;
    len1[l1]=z;
    if(pd[x]||pd[y])
    return;
    else
    {
        l2++;
        pre2[l2]=last2[x];
        last2[x]=l2;
        other2[l2]=y;
        len2[l2]=z;
    }
}

void SPFA1(int s)
{
    memset(dis1,63,sizeof dis1);
    memset(vis1,0,sizeof vis1);
    que1[1]=s;
    dis1[s]=0;
    int h=0,t=1;
    while(h!=t)
    {
        h++;
        int u=que1[h];
        vis1[u]=0;
        for(int p=last1[u];p;p=pre1[p])
        {
            int v=other1[p];
            if(dis1[v]>dis1[u]+len1[p])
            {
                dis1[v]=dis1[u]+len1[p];
                if(!vis1[v])
                {
                    t++;
                    vis1[v]=1;
                    que1[t]=v;
                }
            }
        }
    }
}

void SPFA2(int s)
{
