        for(int p=last2[u];p;p=pre2[p])
        {
            int v=other2[p];
            if(dis2[v]>dis2[u]+len2[p])
            {
                dis2[v]=dis2[u]+len2[p];
                if(!vis2[v])
                {
                    t++;
                    vis2[v]=1;
                    que2[t]=v;
                }
            }
        }
    }
}

int main()
{
