    while(t) {
        if(t&1)r=r*a%M;
        a=a*a%M,t>>=1;
    }
    return r;
}
inline ll C(int n,int m) {
    return fac[n]*inf[m]%M*inf[n-m]%M;
}
int main() {
    cin>>n>>a>>k;
    fac[0]=inv[1]=inf[0]=1;
    for(int i=2; i<=k+1; ++i)inv[i]=inv[M%i]*(M-M/i)%M;
    for(int i=1; i<=k+1; ++i)fac[i]=fac[i-1]*i%M,inf[i]=inf[i-1]*inv[i]%M;
    if(a!=1) {
        iva=pow(a-1,M-2);
