        for(int i=1; i<=k; ++i) {
            T[i]=pow(n,i)*pow(a,n+1)%M-a;
            for(int j=i-1,_=1; j>=0; --j,_=-_)
                T[i]=(T[i]-C(i,j)*_*(T[j]-a))%M;
            T[i]=(T[i]+M)%M*iva%M;
        }
    } else {
        T[0]=n;
        for(int i=1; i<=k; ++i) {
            T[i]=pow(n,i+1)%M;
            for(int j=i-1,_=1; j>=0; --j,_=-_)
                T[i]=(T[i]+C(i+1,j)*_*T[j])%M;
