	Rep(i,0,n)
		_Rep(j,e,0)
			Rep(k,0,d){
				int q=1;
				while(q*q<=j&&k+q<=d){
					f[i][j-q*q][k+q]=min(f[i][j-q*q][k+q],f[i][j][k]+1);
					f[i+1][e-k-q][k+q]=min(f[i+1][e-k-q][k+q],f[i][j][k]+1);
