#include<cstdio>
using namespace std;
int main(){
    int n,k;
    cin>>n;
    for(int i=100;i>=1;--i){
        if((n/52-7*i)%21!=0){
            continue;
        }
        k=(n/52-7*i)/21;
        if(k<=0){
            continue;
        }
        cout<<i<<endl;
        cout<<k<<endl;
