      if(t>sum){
          cout<<sum<<" "<<2<<endl;
          for(int i=1;i<=sum;i++){
              int ans=0;
              for(int i=1;i<=t;i++)
                  if(a[s[i]]>=1)
                      ans++;//现有种类数
              cout<<ans<<" ";
