                  if(a[s[i]]>=1)
                      cout<<ss[i]<<" ",a[s[i]]--;//减掉
              cout<<endl;
          }		
      }
      else {
          cout<<t<<" "<<1<<endl;
          for(int i=1;i<=t;i++){
