#include<bits/stdc++.h>

#define mem(x) memset(x,0,sizeof(x))

using namespace std;

int read(){
   int s = 0,w = 1;
   char ch = getchar();
   while(ch < '0' || ch > '9'){if(ch == '-')w = -1;ch = getchar();}
   while(ch >= '0' && ch <= '9')s = s * 10 + ch - '0',ch = getchar();
   return s * w;
}

int n,m;
int a[1000010];
bool bk[1000010];
int ans;

signed main(){
	cin>>n>>m;
	for(int i = 1;i <= n;i ++)a[i] = read();
	while(m --){
		int op,x,y,w;
		op = read();
		if(op == 1){
			x = read(),w = read();
			a[x] = w - a[x];
			bk[x] = true;
		}
		if(op == 2){
			x = read(),y = read();
			bool flag = false;
			for(int i = x;i <= y;i ++){
				if(bk[i]){
					bk[i] = false;
					ans += a[i];
					printf("%d\n",a[i]);
					a[i] = 0;
					flag = true;
				}
			}
			if(flag)continue;
			int s = 0;
			int num;
			for(int i = x;i <= y;i ++){
				if(a[i] >= s){
					s = a[i];
					num = i;
				}
			}
			ans += s;
			printf("%d\n",a[num]);
			a[num] = 0;
		}
		if(op == 3){
			x = read(),y = read(),w = read();
			for(int i = x;i <= y;i ++)a[i] += w;
		}
	}
	if(ans < 10000)puts("QAQ");
	else if(ans < 10000000)puts("Sakura");
	else puts("ice");
	return 0;
}
```

