int a[100003]; // 表示第i个位置的嘤嘤嘤能量
bool fyw[100003] = { false }; // 表示第i个位置是否有绯狱丸
int main() {
	int n, m, ans = 0;
	cin >> n >> m;
	for (int i = 1; i <= n; i++)
		scanf("%d", &a[i]);
	while(m--) {
