			fyw[x] = true;
		}
		else if (mod == 2) {
			scanf("%d %d", &l, &r);
			int maxp = 0, maxn = 0;
			for (int i = r; i >= l; i--) { // 倒序
				if (fyw[i]) {		// 注意题面中“优先”二字，记得break
					fyw[i] = false;
					maxp = i, maxn = a[i];
