using namespace std;
ll m=1e9+9,n,p,mm,nn,pp,ans=1,t;
int main()
{
    scanf("%lld",&t);
    while(t--)//多组数据读入
    {
		scanf("%lld",&n);
		p=n-1;//质数
		ans=1;//初始值
	    mm=m;nn=n;pp=p;
	    //if(pp==0)return puts("0"),0;
	    for(;p;p/=2,n=n*n%m)if(p&1)ans=ans*n%m;//快速幂
	    printf("%lld\n",ans);//输出
	}
    return 0;
}
