#include<string.h>
#include<algorithm>
#include<stdio.h>
#define maxn 3300009
char data[maxn];
int _length[maxn];
int tot,dirl,dirr,sum,n,k;
void readin()
{
	scanf("%d%d",&n,&k);
	int count=0;
	getchar();
	getchar();
	char c=getchar();//读入优化
	while(c<='Z'&&c>='A') 
	{
		data[count++]=c;
		c=getchar();
	}
	tot=count;
	for(int i=0;i<tot;i++)
	{
	data[i+tot]=data[i];
	data[i+tot*2]=data[i];
	}
	dirl=tot;//拆环
	dirr=tot*2;
	tot*=3;
}
void manacher()
{
	int right=dirl,mid;
	for(int i=dirl;i<dirr;i++)
	{
		if(i<right)
    	_length[i]=std::min(_length[(mid*2)-i],_length[mid]+mid-i);
    	else _length[i]=1;
    	while(data[i+_length[i]]==data[i-_length[i]]) 
    	_length[i]++;
        if(_length[i]+i>right)
        {
            right=_length[i]+i;
            mid=i;
        }	
	}
}
void print()
{
	for(int i=dirl;i<dirr;i++)
	{
		if(k<=_length[i]*2-1) sum++;
	}
	printf("%d",sum);

        return 0;
}
int main()
{
	readin();
	manacher();
	print();
}
```

