    high[0]=0;//地面的高度为零
	cin>>n;
	for(register int i=1;i<=n;i++){
		cin>>high[i];
	}
	sort(high,high+1+n);
    r=n;
    l=0;
    int save=0;
	for(save=0;l<r;save++){
		total+=(high[l]-high[r])*(high[l]-high[r]);l++;
		total+=(high[r]-high[l])*(high[r]-high[l]);r--;
	    }
	cout<<total;
