const int mod=1e9+7;
class mat{
	private:
		int n,m;

	public:
		int a[101][101];
		mat(int x,int y,bool flag=0){
			memset(a,0,sizeof(a));
			n=x;
			m=y;
			if(flag&&n==m)
				for(int i=1;i<=n;i++)
					a[i][i]=1;
