				for(int i=1;i<=n;i++)
					for(int j=1;j<=n;j++)
						ans.a[i][j]=b.a[i][j]+a[i][j],ans.a[i][j]%=mod;
				return ans;
			}
		}
		mat operator*(mat &b){
			if(b.n==m){
				mat ans(n,b.m);
				for(int i=1;i<=n;i++)
					for(int j=1;j<=b.m;j++)
						for(int k=1;k<=m;k++)
