				return ans;
			}
		}
		mat pw(long long t){
			if(n==m){
				mat re(n,n,1);
				if(t==0)
					return re;
				if(t%2)re=re*(*this);
				mat ans(n,n);
				ans=pw(t/2);
				re=re*ans*ans;
				return re;
			}
		}
		void display(){
			for(int i=1;i<=n;i++){
				for(int j=1;j<=m;j++)
