					int x=a[i].niu;
					if((a[i].score[j]+a[i].score[k])%10==0) a[i].niu=10;
					else a[i].niu=max(a[i].niu,(a[i].score[j]+a[i].score[k])%10);
					if(a[i].niu>x) a[i].board=0;
				}
			}
		}
