```cpp
#include <bits/stdc++.h>
using namespace std; 
struct Card {
    int x;//卡牌编号 
    int col;//卡牌花色 

    friend bool operator>(Card a,Card b)//比较 
	{
        return a.x==b.x?a.col<b.col:a.x>b.x;
    }
};
struct Score {
    int Point;//最大分值 
    bool Boom;int Boom_Num;//是否有炸弹，有的话多大 
    bool Board;int Board_Num;//是否有铁板，铁板的数值 
    Card Num;//最大值
};
int T;
int n;
string Name[100001];
map<string,int>points;//姓名与分数的映射，结构体也行 
void fu_zhi(Score&a)
{
    a.Point=a.Boom_Num = a.Board_Num = 0;
    a.Boom=a.Board=false;
    a.Num=(Card){0, 0};
}
bool cmp(Card a,Card b)//比较大小 
{
    return a > b;
}
Score Get()
{
    Score Have;fu_zhi(Have);
    Card a[5];
    char str[5];
    int sum=0;
    for(int i=0;i<5;i++)
	{
        str[0]=str[1]=str[2]=0;//清空 
        cin>>str;//输入 
        a[i].col=str[0]-'a';//花色 
        a[i].x=str[2]=='0'?10:(str[1]=='A'?1:str[1]-48);
        //牌数 
        sum+=a[i].x;
    }
    bool OK=false;
    sort(a,a+5,cmp);//排一下序 
    Have.Num=a[0];//最大值 
    //检查是否有炸弹及赋分 
    if(a[1].x==a[2].x&&a[2].x==a[3].x)
        if(a[0].x==a[1].x||a[4].x==a[1].x)
		{
            Have.Boom=1;
            Have.Boom_Num=a[1].x;
            return Have;
        }
    //检查是否有分 
    int Point=0;
    for(int i=0;i<5;i++)
        for(int j=i+1;j<5;j++)
            if((a[i].x+a[j].x)%10==sum%10)
			{
                OK=true;
                break;
            }
    if(OK)Point=(sum-1)%10+1;//有分 
    bool Board=false;
    int Num=0;
    int Sum=0;
    //是否有铁板及赋分 
    for(int i=0;i<3;i++)
        if(a[i].x==a[i+1].x&&a[i+1].x==a[i+2].x)
		{
            Board=true;
            Num=a[i].x;
            Sum=sum-a[i].x*3;
        }
    Sum=(Sum-1)%10+1;
    if(!OK&&!Board)//无牛 
	{
        return Have;
    }
    if(Point<=Sum)//铁板是最大的时 
	{
        Have.Board=true;
        Have.Board_Num=Num;
        Have.Point=Sum;
        return Have;
    }
    else//正常是最大的时 
	{
        Have.Point = Point;
        return Have;
    }
}
void calc(string a,Score A,string b,Score B)
{
    if(A.Boom||B.Boom)//都有炸弹 
	{
        if(A.Boom_Num>B.Boom_Num)//A比B大 
		{
            points[a]+=100;
            points[b]-=100;
        }
        else//A比B小 
		{
            points[a] -= 100;
            points[b] += 100;
