        if(A.Point>B.Point||(A.Point==B.Point&&((A.Board&&!B.Board)||(A.Board&&B.Board&&A.Board_Num>B.Board_Num)||(!A.Board&&!B.Board&&A.Num>B.Num))))
		{//总而言之，A大于B的所有情况时，计分 
            int mul=1;
            if(A.Board)mul*=2;
            if(A.Point>=7)mul*=2;
