        }
        else//B大于A时 
		{
            int mul=1;
            if(B.Board)mul*=2;
            if(B.Point>=7)mul*=2;
            if(B.Point==10)mul=(mul>>1)*3;
            points[a]-=mul*10;
            points[b]+=mul*10;
        }
    }
}
