```
#include <bits/stdc++.h>
using namespace std;
long long n,m,p1,s1,s2,ans;
long long dra=0,tig=0,cha;
int pd(int a){
    if(dra-tig>0){
        if(a+m>=n)return n;
        else return m+a;
    }
    else if(dra-tig<0){
        if(m-a<=1)return 1;
        else return m-a;
    }
    else return m;
        return 0;
}

int main(){
    cin>>n;
    int a[n+1];
    for(int i=1;i<=n;i++)scanf("%d",&a[i]);
    cin>>m>>p1>>s1>>s2;
    a[p1]=a[p1]+s1;
    for(int i=1;i<m;i++)dra+=a[i]*(m-i);
    for(int i=m+1;i<=n;i++)tig+=a[i]*(i-m);
    cha=abs(dra-tig);
    ans=cha/s2;
    if(cha%s2==0){
        cout<<pd(ans);
        return 0;
    }
    else{
        if(abs(cha-ans*s2)<abs(cha-(ans+1)*s2)){
            cout<<pd(ans);
        return 0;
        }
        else{
        	cout<<pd(ans+1);
        	return 0;
        }
    }
    return 0;
}
