#include <cstdio>
long long a[1000001];
int main()
{
	long long n=0,m=0,p1=0,s1=0,s2=0LL;
	scanf("%lld",&n);
	for(long long i=1;i<=n;i++)
	{
		scanf("%lld",&a[i]);
	}
	scanf("%lld %lld %lld %lld",&m,&p1,&s1,&s2);
	a[p1]+=s1;
	long long ta=0,da=0,tb=0,db=0LL; 
	for(long long i=1;i<=n;i++)
	{
		if(i<m)
		{
			ta+=a[i]*(m-i);
			da+=ta/1000000LL;
			ta%=1000000LL;
		}
		else if(i>m)
		{
			tb+=a[i]*(i-m);
			db+=tb/1000000LL;
			tb%=1000000LL;
		}
	}
	long long ans=m,tc=0,dc=0LL;
	if(da>db)
	{
		dc=da-db;
		tc=ta-tb;
		if(tc<0LL)
		{
			tc+=1000000LL;
			dc--;
		}
	}
	else if(da==db)
	{
		if(ta>tb)
		{
			dc=0LL;
			tc=ta-tb;
		}
		if(tb>ta)
		{
			dc=0LL;
			tc=tb-ta;
		}
	}
	else if(da<db)
	{
		dc=db-da;
		tc=tb-ta;
		if(tc<0LL)
		{
			tc+=1000000LL;
			dc--;
		}
	}
	for(long long i=1;i<=n;i++)
	{
		long long dqta=ta,dqda=da,dqtb=tb,dqdb=db;
		if(i<m)
		{
			dqta+=s2*(m-i);
		}
		else if(i>m)
		{
			dqtb+=s2*(i-m);
		}
		dqda+=dqta/1000000LL;
		dqta%=1000000LL;
		dqdb+=dqtb/1000000LL;
		dqtb%=1000000LL;
		long long dqtc=0,dqdc=0LL;
		if(dqda>dqdb)
		{
			dqdc=dqda-dqdb;
			dqtc=dqta-dqtb;
			if(dqtc<0LL)
			{
				dqtc+=1000000LL;
				dqdc--;
			}
		}
		else if(dqda==dqdb)
		{
			if(dqta>dqtb)
			{
				dqdc=0LL;
				dqtc=dqta-dqtb;
			}
			if(dqtb>dqta)
			{
				dqdc=0LL;
				dqtc=dqtb-dqta;
			}
		}
		else if(dqda<dqdb)
		{
			dqdc=dqdb-dqda;
			dqtc=dqtb-dqta;
			if(dqtc<0LL)
			{
				dqtc+=1000000LL;
				dqdc--;
			}
		}
		if(dqdc<dc)
		{
			ans=i;
			dc=dqdc;
			tc=dqtc;
		}
		else if(dqdc==dc)
		{
			if(dqtc<tc)
			{
				ans=i;
				dc=dqdc;
				tc=dqtc;
			}
			else if(dqtc==tc)
			{
				if(ans>i)
				{
					ans=i;
				}
			}
		}
	}
	printf("%lld",ans);
	return 0LL;
}
