#include <iostream>
#include <cstdio>
#include <cstring>
#include <algorithm>
#include <vector>
#include <queue>
#include <stack>
#include <climits>
#include <cstdlib>
#include <cmath>
#include <map>
#define db cout << "*********" << endl
#define dbi(x) cout << x << endl
#define dbc(x) cout <<"x"<< endl
#define maxn 100005 
#define INF (1<<25)
#define LL long long
#define ULL unsingned long long
using namespace std;
int N;
LL cnt; 
int d[maxn];

void work(int l,int r,int tag){
	vector<int> v;
	if(l > r)return;
	if(l == r){
		cnt += d[l] - tag;
		return;
	}
	int mind = INF;
	for(int i = l;i <= r;i++){
		mind = min(mind,d[i]);
	}
	for(int i = l;i <= r;i++){
		if(d[i] == mind){
			v.push_back(i);
		}
	}
	cnt += mind - tag;
	work(l,v[0]-1,mind);
	for(int i = 0;i < v.size()-1;i++){
		work(v[i]+1,v[i+1]-1,mind);
	}
	work(v[v.size()-1]+1,r,mind);
	return;
}

int main(){
	scanf("%d",&N);
	for(int i = 1;i <= N;i++){
		scanf("%d",d+i);
	}
	work(1,N,0);
	printf("%lld",cnt,0);
	return 0;
}
```

