#### _其余的请看代码~_

```cpp
#include<iostream>
#include<queue>
using namespace std;

int n;
int a[100003];

int dfs(int l,int r)
{
	int ans = 0;
	if (l<1 || l>n || r<0 || r>n || l > r)
		return 0;
	if (l == r)
		return a[l];
	int i = l;
	while (i <= r)
	{
		int min = a[i];
		while (a[i] != 0 && i <= r)
		{
			min = min > a[i] ? a[i] : min;
			i++;
		}
		ans += min;
		for (int j = l; j <= r; j++)
			a[j] -= min;
		queue<int>Q;
		Q.push(l - 1);
		for (int j = l; j <= r; j++)
			if (a[j] == 0)
				Q.push(j);
		Q.push(r + 1);
		while (Q.size() != 1)
		{
			int a = Q.front() + 1;
			Q.pop();
			int b = Q.front() - 1;
			ans += dfs(a, b);
		}
	}
	return ans;
}

int main()
{
	cin >> n;
	for (int i = 1; i <= n; i++)
		cin >> a[i];
	cout << dfs(1, n);
	return 0;
}
```
