typedef long long ll;
const int N=1e5+7;
int n,sum,max1,a[N],die[N];
ll ans;
vector<int>vec[N];
void dele(int x)
{
	if(x==1)
	{
		if(die[x+1])sum--;
	}
	else if(x==n)
	{
		if(die[x-1])sum--;
	}
	else{
		if(die[x-1]&&die[x+1])sum--;
		else if(!die[x-1]&&!die[x+1])sum++;
	}
	die[x]=1;
}
int main()
{
	freopen("road.in","r",stdin);
	freopen("road.out","w",stdout);
