```cpp

#include<cstdio>
#include<algorithm>
#include<cmath>
#include<iostream>
#include<cstring>
#include<string>
using namespace std;
int n,d[100010],sum;
int main()
{
	scanf("%d",&n);
	for(int i=1;i<=n;i++)
	{
		scanf("%d",&d[i]);
		sum+=d[i];//sum记录总深度
	}
	long long day=0,h=0;//h来记录前驱节点，一定要赋值为零！否则在一些神数据中会多加一个1.
	while(day>-1)
	{
		for(int j=1;j<=n+1;j++)
		{
			if(d[j]>0)
			{
				h=d[j];//记录前驱节点
				d[j]-=1;//深度-1
				sum-=1;//总深度-1
			}
			else if(d[j]==0&&h!=0)//一定要h不为零才能加，否则要是区间全是0的话不知道多加多少个1.
			{
				day+=1;
				h=0;//重新赋为0.
			}
		}
		if(sum==0)
		break;
	}
	cout<<day;
	return 0;
}

