void solve(){
    memset(F.a,0,sizeof(F.a));
    int n,m = 0,ans;
    read(n);
    ans = n;
    for(reg int i=1;i<=n;++i){
        read(b[i]);
        m = max(m,b[i]);
        ++st[b[i]]; //桶
    }
    F.t = m;
    for(reg int i=1;i<=m;++i){ //枚举面值
        if(!st[i]) continue;
        for(reg int j=1;j*i<=m;++j)
            F.a[i*j] = (F.a[i*j]+(ll)inv[j]*st[i])%p; //将物品的多项式ln加起来
        st[i] = 0;    
    }
    F = exp(F); //多项式exp
    for(reg int i=0;i<=n;++i)
        if(F.a[b[i]]>1) --ans; //如果能被多于1种方式表示,那就是多余的
    print(ans),putchar('\n');
}

