###### 可以算是三种做法吧
```cpp
#include <iostream>
#include <algorithm>
using namespace std;
const int inf = 0x3f3f3f3f;
const int N = 110, MAXN = 25005;
int t, n, a[N];

int f[N][MAXN];
void doDp1() {
	for(int i = 0; i <= n; i++)
		for(int j = 1; j <= a[n]; j++)
			f[i][j] = -inf;

	for(int i = 1; i <= n; i++)
		for(int j = 1; j <= a[n]; j++)
			for(int k = 0; k <= j / a[i]; k++)
				f[i][j] = max(f[i][j], f[i - 1][j - k * a[i]] + k);

	int cnt = n;

	for(int i = 1; i <= n; i++)
		if(f[i][a[i]] > 1)
			cnt--;

	cout << cnt << endl;
}

int f2[MAXN];
void doDp2() {
	for(int i = 1; i <= a[n]; i++)
		f2[i] = -inf;

	for(int i = 1; i <= n; i++)
		for(int j = a[i]; j <= a[n]; j++)
			f2[j] = max(f2[j], f2[j - a[i]] + 1);

	int cnt = n;

	for(int i = 1; i <= n; i++)
		if(f2[a[i]] > 1)
			cnt--;

	cout << cnt << endl;
}

void doDp3() {
	for(int i = 0; i <= n; i++)
		for(int j = 1; j <= a[n]; j++)
			f[i][j] = inf;

	for(int i = 1; i <= n; i++)
		for(int j = 1; j <= a[n]; j++)
			for(int k = 0; k <= j / a[i]; k++)
				f[i][j] = min(f[i][j], f[i - 1][j - k * a[i]] + k);

	int cnt = n;

	for(int i = 1; i <= n; i++)
		if(f[i - 1][a[i]] < inf && f[i - 1][a[i]] > 0)
			cnt--;

	cout << cnt << endl;
}

int main() {
	cin >> t;

	for(int i = 1; i <= t; i++) {
		cin >> n;

		for(int j = 1; j <= n; j++)
			cin >> a[j];

		sort(a + 1, a + n + 1);
		//doDp1();
		//doDp2();
		doDp3();
	}

	return 0;
}


