接 zhoutb 的思路，由于询问是离线的，这道题可以把倍增部分替换成并查集实现，即：倍增要解决的问题是每次询问一条链的 dp 值（或者说这个 min-plus 矩阵的积），这可以考虑一个类似 tarjan lca 的过程，结合路径压缩。但是貌似不太支持按秩合并……通过 wiki 来的复杂度，该这种情况下的复杂度为 $\Theta(n + m\log_{2 + m/n} n)$。

```cpp
#include <cstdio>
#include <cmath>
#include <ctime>
#include <cstring>
#include <cstdlib>

#include <algorithm>
#include <numeric>
#include <limits>
#include <functional>
#include <stack>
#include <vector>
#include <set>
#include <map>
#include <queue>

#define LOG(FMT...) fprintf(stderr, FMT)

using namespace std;

typedef long long ll;
typedef unsigned long long ull;

struct Edge {
    int v;
    Edge *next;
};

ll ad(ll x, ll y) {
    if (~x & ~y)
        return x + y;
    return -1;
}

bool cmp(ll x, ll y) {
    return x != -1 && (x < y || y == -1);
}

struct Mat {
    ll gr[2][2];

    Mat() { memset(gr, -1, sizeof(gr)); }

    Mat(const Mat& rhs) { memcpy(gr, rhs.gr, sizeof(gr)); }

    ll* operator[](int k) { return gr[k]; }
    const ll* operator[](int k) const { return gr[k]; }

    void dbg() const {
        for(int i = 0; i < 2; ++i) {
            for(int j = 0; j < 2; ++j) {
                LOG("%lld ", gr[i][j]);
            }
            LOG("\n");
        }
    }

    Mat operator*(const Mat& rhs) const {
        Mat m = Mat();
        for (int i = 0; i < 2; ++i)
            for (int j = 0; j < 2; ++j) {
                for (int k = 0; k < 2; ++k)
                    m[i][j] = min(m[i][j], ad(gr[i][k], rhs.gr[k][j]), cmp);
            }
//		dbg();
//		rhs.dbg();
//		m.dbg();
        return m;
    }
};

const int N = 100010;

int n, m;
char s[5];
bool vis[N];
int p[N], f[N], qa[N], qx[N], qb[N], qy[N], lca[N];
Mat dt[N];
ll ans[N];
ll dp[N][2], tmp[N][2];
Edge* g[N];
vector<pair<int, int> > ql[N];
vector<int> qr[N];

int find(int x) { return f[x] == x ? x : f[x] = find(f[x]); }

int find2(int x) {
    if (f[x] == x)
        return x;
    int prt = f[x];
    f[x] = find2(prt);
    dt[x] = dt[prt] * dt[x];
    return f[x];
}

void adde(int u, int v);
void tarjan(int u);
void dfs(int u);

int main() {
#ifndef ONLINE_JUDGE
    freopen("test.in", "r", stdin);
    int nol_cl = clock();
#endif

    scanf("%d%d%s", &n, &m, s);
    for (int i = 1; i <= n; ++i)
        scanf("%d", &p[i]);
    for (int rep = 1; rep < n; ++rep) {
        int u, v;
        scanf("%d%d", &u, &v);
        adde(u, v);
        adde(v, u);
    }
    for (int i = 1; i <= m; ++i) {
        scanf("%d%d%d%d", &qa[i], &qx[i], &qb[i], &qy[i]);
        ql[qa[i]].push_back(make_pair(qb[i], i));
        ql[qb[i]].push_back(make_pair(qa[i], i));
    }
    tarjan(1);
    for (int i = 1; i <= m; ++i)
        qr[lca[i]].push_back(i);
    memset(vis, 0, sizeof(vis));
    memset(f, 0, sizeof(f));
    {
        Mat model = Mat();
        model[0][0] = model[1][1] = 0;
        fill(dt + 1, dt + n + 1, model);
    }
    dfs(1);
    for (int i = 1; i <= m; ++i) {
        int lc = lca[i];
        find2(lc);
        ans[i] = -1;
//		LOG("%d: %lld %lld\n", i, tmp[i][0], tmp[i][1]);;
        for (int k = 0; k < 2; ++k)
            for (int j = 0; j < 2; ++j)
                ans[i] = min(ans[i], ad(dt[lc][k][j], tmp[i][j]), cmp);
        printf("%lld\n", ans[i]);
    }

#ifndef ONLINE_JUDGE
    LOG("Time: %dms\n", int((clock() - nol_cl) / (double)CLOCKS_PER_SEC * 1000));
#endif
    return 0;
}

void dfs(int u) {
    vis[u] = true;
    f[u] = u;
    dp[u][1] = p[u];
    for (Edge* p = g[u]; p; p = p->next)
        if (!vis[p->v]) {
            dfs(p->v);
            dp[u][0] += dp[p->v][1];
            dp[u][1] += min(dp[p->v][0], dp[p->v][1]);
        }
    for (int i = 0; i < qr[u].size(); ++i) {
        int id = qr[u][i];
        int &a = qa[id], &x = qx[id], &b = qb[id], &y = qy[id];
        if (u == b) {
            swap(a, b);
            swap(x, y);
        }
        if (u != a) {
            find2(a);
            find2(b);
            for (int k = 0; k < 2; ++k) {
                tmp[id][k] = -1;
                ll sub = dp[u][k];
                if (k == 0)
                    sub -= dp[f[a]][1] + dp[f[b]][1];
                else
                    sub -= min(dp[f[a]][0], dp[f[a]][1]) + min(dp[f[b]][0], dp[f[b]][1]);

                if (k == 0)
                    tmp[id][0] = ad(dp[a][x] + dp[b][y], ad(sub, ad(dt[a][1][x], dt[b][1][y])));
                else
                    tmp[id][1] = ad(dp[a][x] + dp[b][y] + sub, ad(min(dt[a][1][x], dt[a][0][x], cmp), min(dt[b][1][y], dt[b][0][y], cmp)));
            }
        }
    }
    for (Edge* p = g[u]; p; p = p->next)
        if (!vis[p->v]) {
            f[p->v] = u;
            dt[p->v][0][0] = -1;
            dt[p->v][0][1] = dp[u][0] - dp[p->v][1];
            dt[p->v][1][0] = dt[p->v][1][1] = dp[u][1] - min(dp[p->v][0], dp[p->v][1]);
        }
    for (int i = 0; i < qr[u].size(); ++i) {
        int id = qr[u][i];
        int a = qa[id], x = qx[id], b = qb[id], y = qy[id];
        if (u == a) {
            find2(b);
//			LOG("%d(from %d)%d %d\n", f[b], b, f[f[b]], u);
            tmp[id][x] = ad(dt[b][x][y], dp[b][y]);
//			LOG("%lld\n", dt[b][x][y]);
            tmp[id][!x] = -1;
        }
    }
    vis[u] = false;
}

void tarjan(int u) {
    static bool vis[N];
    vis[u] = true;
    f[u] = u;
    for (Edge* p = g[u]; p; p = p->next)
        if (!vis[p->v]) {
            tarjan(p->v);
            f[p->v] = u;
        }
    for (int i = 0; i < ql[u].size(); ++i)
        if (f[ql[u][i].first])
            lca[ql[u][i].second] = find(ql[u][i].first);
}

void adde(int u, int v) {
    static Edge pool[N * 2];
    static Edge* p = pool;
    p->v = v;
    p->next = g[u];
    g[u] = p;
    ++p;
}
```

考前几个星期曾经了解过这个神奇的动态DP，然而并没有认真去看。

于是考场上放弃治疗48~52分滚粗。

回学校后认真的看了矩阵的构造方法，并写了一遍模板。

~~然而这样补救并不能挽回我的失分~~

令$f[i][0]$表示第i个点不驻扎军队时以i为根节点的子树的最小代价，$f[i][1]$表示第i个点驻扎军队时以i为根节点的子树的最小代价，我们可以得到状态转移方程：

$f[i][0] = \sum f[son][1],f[i][1]=a[i]+\sum min(f[son][0],f[son][1])$。

但是这样的DP要怎么支持快速修改呢???

令$g[i][0]$表示第i个点不驻扎军队时以i为根节点的子树(但不包括以重儿子为根的子树)内的最小代价，$g[i][1]$表示第i个点驻扎军队时以i为根节点的子树(但不包括以重儿子为根的子树)内的最小代价。($hson$表示重儿子)

这样就有$g[i][0]=\sum f[son][0] (son \neq hson),g[i][1]=a[i]+\sum min(f[son][0],f[son][1])(son \neq hson)$。

这样修改一个节点时，只会修改这个节点到根节点的路径上所有轻边处的$g$值。

但是怎么快速得到某个重链链顶节点的$f$值呢?


不难发现$f[i][0]=g[i][0]+f[hson][1],f[i][1]=g[i][1]+min(f[hson][0],f[hson][1])$。

我们修改一下矩阵乘法的定义，将原来的加法改成取min，乘法改成加法，不难发现修改后的矩阵乘法仍然满足结合律。

可以构造出以下矩阵，套用定义和转移方程，不难发现它是对的

$\begin{bmatrix}\infty&g[x][0]\\g[x][1]&g[x][1]\end{bmatrix}*\begin{bmatrix}f[hson][0]\\f[hson][1]\end{bmatrix}=\begin{bmatrix}f[x][0]\\f[x][1]\end{bmatrix}$

对于没有重儿子的结点，将它的重儿子的$f$值理解成$f[hson][0]=f[hson][1]=0$。

这样一来查询某个重链链顶节点的$f$值只要查询这个重链链顶节点到它所在重链的链底的路径上的矩阵乘积即可，可以使用线段树维护区间矩阵乘积。

当修改一个节点的权值时，需要更新它到根节点上所有轻边处的$g$值。

在这个问题中，强制不选一个节点的方法是将这个节点的权值强制加上$\infty$，强制选一个节点的方法是将这个节点的权值强制减去$\infty$。

于是就有了下面的代码：

```cpp
#include<cstdio>
#include<algorithm>
#include<string>
#include<iostream>
using namespace std;
#define maxn 100100
#define int long long
const int inf=1e18;int n,m;
int head[maxn],nxt[maxn<<1],ver[maxn<<1],tot;
inline void addedge(int a,int b)
{
	nxt[++tot]=head[a];ver[tot]=b;head[a]=tot;
	nxt[++tot]=head[b];ver[tot]=a;head[b]=tot;
}
int f[maxn][2],V[maxn];
inline void treedp(int x,int fat)
{
	f[x][0]=0;f[x][1]=V[x];
	for(int i=head[x];i;i=nxt[i])
	{
		int y=ver[i];if(y==fat) continue;
		treedp(y,x);
		f[x][0]+=f[y][1];
		f[x][1]+=min(f[y][0],f[y][1]);
	}
}
int fa[maxn],dep[maxn],size[maxn],hson[maxn];
inline void dfs1(int x,int fat)
{
	fa[x]=fat;dep[x]=dep[fat]+1;size[x]=1;int maxsize=0;
	for(int i=head[x];i;i=nxt[i])
	{
		int y=ver[i];if(y==fat) continue;
		dfs1(y,x);size[x]+=size[y];
		if(size[y]>maxsize) maxsize=size[y],hson[x]=y;
	}
}
int top[maxn],id[maxn],rid[maxn],fot[maxn],id_cnt;
inline void dfs2(int x,int Top)
{
	top[x]=Top;id[x]=++id_cnt;rid[id_cnt]=x;
	if(hson[x]) dfs2(hson[x],Top);
	for(int i=head[x];i;i=nxt[i])
	{
		int y=ver[i];if(y==fa[x]||y==hson[x]) continue;
		dfs2(y,y);
	}
}
struct Matrix
{
	int v[2][2];
	Matrix(){v[0][0]=v[0][1]=v[1][0]=v[1][1]=inf;}
	Matrix operator *(const Matrix &p)const
	{
		Matrix ans;
		for(int i=0;i<=1;i++) for(int k=0;k<=1;k++) for(int j=0;j<=1;j++) ans.v[i][j]=min(ans.v[i][j],v[i][k]+p.v[k][j]);
		return ans;
	}
};
Matrix val[maxn];
struct segment_tree
{
	#define ls (p<<1)
	#define rs (p<<1|1)
	Matrix tr[maxn<<2];
	inline void pushup(int p){tr[p]=tr[ls]*tr[rs];}
	inline void build(int p,int l,int r)
	{
		if(l==r)
		{
			int x=rid[l],g0=0,g1=V[x];
			for(int i=head[x];i;i=nxt[i])
			{
				int y=ver[i];if(y==fa[x]||y==hson[x]) continue;
				g0+=f[y][1];g1+=min(f[y][0],f[y][1]);
			}
			tr[p].v[0][0]=inf;tr[p].v[0][1]=g0;
			tr[p].v[1][0]=tr[p].v[1][1]=g1;
			val[l]=tr[p];return;
		}
		int mid=(l+r)>>1;
		build(ls,l,mid);
		build(rs,mid+1,r);
		pushup(p);
	}
	inline void update(int p,int l,int r,int kkk)
	{
		if(l==r){tr[p]=val[l];return;}
		int mid=(l+r)>>1;
		if(kkk<=mid) update(ls,l,mid,kkk);
		else update(rs,mid+1,r,kkk);
		pushup(p);
	}
	inline Matrix query(int p,int l,int r,int ql,int qr)
	{
		if(ql<=l&&qr>=r) return tr[p];
		int mid=(l+r)>>1;
		if(qr<=mid) return query(ls,l,mid,ql,qr);
		if(ql>mid) return query(rs,mid+1,r,ql,qr);
		return query(ls,l,mid,ql,qr)*query(rs,mid+1,r,ql,qr);
	}
}seg;
inline void init()
{
	scanf("%lld%lld",&n,&m);string s;cin>>s;
	for(int i=1;i<=n;i++) scanf("%lld",V+i);
	for(int i=1,a,b;i<n;i++) scanf("%lld%lld",&a,&b),addedge(a,b);
	treedp(1,0);dfs1(1,0);dfs2(1,1);
	for(int i=1;i<=n;i++) if(top[i]==i){int t=i;while(hson[t]) t=hson[t];fot[i]=t;}
	seg.build(1,1,n);
}
inline Matrix query(int x){return seg.query(1,1,n,id[x],id[fot[x]]);}
inline int getans()
{
	Matrix t=query(1);
	return min(t.v[0][1],t.v[1][1]);
}
inline void change(int x,int to)
{
	val[id[x]].v[1][0]-=V[x]-to;val[id[x]].v[1][1]-=V[x]-to;V[x]=to;
	while(x)
	{
		Matrix old=query(top[x]);
		seg.update(1,1,n,id[x]);
		Matrix nxt=query(top[x]);
		x=fa[top[x]];
		val[id[x]].v[0][1]+=nxt.v[1][1]-old.v[1][1];
		val[id[x]].v[1][0]+=min(nxt.v[1][1],nxt.v[0][1])-min(old.v[1][1],old.v[0][1]);
		val[id[x]].v[1][1]=val[id[x]].v[1][0];
	}
}
inline void getquery(int x1,int t1,int x2,int t2)
{
	if(t1==0&&t2==0&&(fa[x1]==x2||fa[x2]==x1)){puts("-1");return;}
	int v1=V[x1],v2=V[x2];
	change(x1,t1?v1-inf:v1+inf);
	change(x2,t2?v2-inf:v2+inf);
	int res=getans();
	res+=t1?inf:0;
	res+=t2?inf:0;
	printf("%lld\n",res);
	change(x1,v1);
	change(x2,v2);
}
#undef int
int main()
#define int long long
{
	init();
	while(m--)
	{
		int a,b,c,d;scanf("%lld%lld%lld%lld",&a,&b,&c,&d);
		getquery(a,b,c,d);
	}
	return 0;
}
```
复杂度$2^3nlog^2n$。~~人傻自带大常数，不开O2会TLE~~

可以把树链剖分换成LCT少一只log，然而常数巨大并不能跑过实现得好一点的树剖。

注意这个LCT没有换根操作，且Access的时候断开原有的splay上儿子时要在这个节点的$g$值中加上splay上儿子的贡献，把新的儿子接上去时要在$g$值中减去新的儿子的贡献。

~~这下不开O2也能过了~~

上代码：

```cpp
#include<bits/stdc++.h>
using namespace std;
#define maxn 100100
#define int long long
const int inf=1e18;
struct Matrix
{
	int v[2][2];
	Matrix(){v[0][0]=v[0][1]=v[1][0]=v[1][1]=inf;}
	Matrix operator *(const Matrix &p)const
	{
		Matrix ans;
		for(int i=0;i<=1;i++) for(int k=0;k<=1;k++) for(int j=0;j<=1;j++) ans.v[i][j]=min(ans.v[i][j],v[i][k]+p.v[k][j]);
		return ans;
	}
};
int head[maxn],nxt[maxn<<1],ver[maxn<<1],tot;
inline void addedge(int a,int b)
{
	nxt[++tot]=head[a];ver[tot]=b;head[a]=tot;
	nxt[++tot]=head[b];ver[tot]=a;head[b]=tot;
}
int V[maxn];int f[maxn][2],fa[maxn];int n,m;
int lstans;
inline void treedp(int x,int fat)
{
	fa[x]=fat;f[x][0]=0;f[x][1]=V[x];
	for(int i=head[x];i;i=nxt[i])
	{
		int y=ver[i];if(y==fat) continue;
		treedp(y,x);
		f[x][0]+=f[y][1];
		f[x][1]+=min(f[y][0],f[y][1]);
	}
}
struct LinkCutTree
{
	int ch[maxn][2],fa[maxn];Matrix g[maxn],mul[maxn];
	inline bool nroot(int p){return ch[fa[p]][0]==p||ch[fa[p]][1]==p;}
	inline void pushup(int p)
	{
		if(ch[p][0]) mul[p]=mul[ch[p][0]]*g[p];else mul[p]=g[p];
		if(ch[p][1]) mul[p]=mul[p]*mul[ch[p][1]];
	}
	inline void rotate(int p)
	{
		int f=fa[p],ff=fa[f],t=(ch[f][1]==p),w=ch[p][t^1];
		if(nroot(f)) ch[ff][ch[ff][1]==f]=p;ch[p][t^1]=f;ch[f][t]=w;
		if(w) fa[w]=f;fa[f]=p;fa[p]=ff;pushup(f);pushup(p);
	}
	inline void splay(int p)
	{
		while(nroot(p))
		{
			int f=fa[p],ff=fa[f];
			if(nroot(f)) rotate((ch[f][0]==p)^(ch[ff][0]==f)?p:f);
			rotate(p);
		}
	}
	inline void access(int x)
	{
		for(int y=0;x;x=fa[y=x])
		{
			splay(x);
			Matrix nxt=mul[ch[x][1]];
			if(ch[x][1]==0) memset(nxt.v,0,sizeof(nxt.v));
			ch[x][1]=y;
			Matrix old=mul[ch[x][1]];
			if(ch[x][1]==0) memset(old.v,0,sizeof(old.v));
			g[x].v[0][1]+=nxt.v[1][1]-old.v[1][1];
			g[x].v[1][0]+=min(nxt.v[1][1],nxt.v[0][1])-min(old.v[1][1],old.v[0][1]);
			g[x].v[1][1]=g[x].v[1][0];
			pushup(x);
		}
	}
	inline void change(int x,int to)
	{
		access(x);splay(x);
		g[x].v[1][0]-=V[x]-to;g[x].v[1][1]-=V[x]-to;
		V[x]=to;pushup(x);
	}
	inline int getans()
	{
		splay(1);Matrix t=mul[1];
		return min(t.v[0][1],t.v[1][1]);
	}
}lct;
inline void init()
{
	scanf("%lld%lld",&n,&m);string s;cin>>s;
	for(int i=1;i<=n;i++) scanf("%lld",V+i);
	for(int i=1,a,b;i<n;i++) scanf("%lld%lld",&a,&b),addedge(a,b);
	treedp(1,0);
	for(int i=1;i<=n;i++)
	{
		lct.fa[i]=fa[i];int g0=0,g1=V[i];
		for(int j=head[i];j;j=nxt[j])
		{
			int y=ver[j];if(y==fa[i]) continue;
			g0+=f[y][1];g1+=min(f[y][0],f[y][1]);
		}
		lct.g[i].v[0][0]=inf;lct.g[i].v[0][1]=g0;
		lct.g[i].v[1][0]=lct.g[i].v[1][1]=g1;lct.pushup(i);
	}
}
inline void getquery(int x1,int t1,int x2,int t2)
{
	if(t1==0&&t2==0&&(fa[x1]==x2||fa[x2]==x1)){puts("-1");return;}
	int v1=V[x1],v2=V[x2];
	lct.change(x1,t1?v1-inf:v1+inf);
	lct.change(x2,t2?v2-inf:v2+inf);
	int res=lct.getans();
	res+=t1?inf:0;
	res+=t2?inf:0;
	printf("%lld\n",res);
	lct.change(x1,v1);
	lct.change(x2,v2);
}
signed main()
{
	init();
	while(m--)
	{
		int a,b,c,d;scanf("%lld%lld%lld%lld",&a,&b,&c,&d);
		getquery(a,b,c,d);
	}
	return 0;
}
