void dfs(int u,int fa)
{
	ri v;
	f[u][0]=f[u][1]=0;
	for(ri i=0;i<(int)e[u].size();++i)
	{
		v=e[u][i];
		if(v!=fa)
		{
			dfs(v,u);
			f[u][0]+=f[v][1];
			f[u][1]+=min(f[v][0],f[v][1]);
		}
	}
	f[u][1]+=p[u];
	if(u==a)f[u][x^1]=inf;
	if(u==b)f[u][y^1]=inf;
}

