using namespace std;

int n,m;
int head[maxn],cnt;
int val[maxn],deep[maxn],father[maxn];
char type[100];
ll f[maxn][2],ff[maxn][2],dp[maxn][2];

struct Edge{
	int u,v;
	Edge(int a = 0,int b = 0){
		u = head[a];
		v = b;
	}
}e[maxn<<1];

struct Rest{
	int node1,node2;
	int val1,val2;
}rest[maxn];

void Read(){
	int a,b;
	cin >> n >> m >> type;
	for(int i = 1;i <= n;i++){
		cin >> val[i];
	}
	for(int i = 1;i <= (n-1);i++){
		cin >> a >> b;
		e[++cnt] = Edge(a,b);
		head[a] = cnt;
		e[++cnt] = Edge(b,a);
		head[b] = cnt;
	}
	for(int i = 1;i <= m;i++){
		cin >> rest[i].node1 >> rest[i].val1;
		cin >> rest[i].node2 >> rest[i].val2;
	}
}

void dfs1(int u,int fa){
	f[u][0] = 0;
	f[u][1] = val[u];

	father[u] = fa;
	deep[u] = deep[fa] + 1;

	for(int i = head[u];i;i = e[i].u){
		int ev = e[i].v;
		if(ev == fa) continue;
		dfs1(ev,u);
		f[u][0] += f[ev][1];
		f[u][1] += min(f[ev][0],f[ev][1]);
	}
}

void dfs2(int u,int fa){
	for(int i = head[u];i;i = e[i].u){
		int ev = e[i].v;
		if(ev == fa) continue;

		ff[ev][0] = ff[u][1] + f[u][1] - min(f[ev][0],f[ev][1]);
		ff[ev][1] = min(ff[u][0] + f[u][0] - f[ev][1],ff[ev][0]);
		dfs2(ev,u);
	}
}

void trans(int id){
	int a = rest[id].node1;
	int b = rest[id].node2;
	int x = rest[id].val1;
	int y = rest[id].val2;
	memset(dp,sizeof(dp),0);

	if(x == 0){
		dp[a][0] = f[a][0];
		dp[a][1] = INF;
	} else {
		dp[a][1] = f[a][1];
		dp[a][0] = INF;
	}
	if(y == 0){
		dp[b][0] = f[b][0];
		dp[b][1] = INF;
	} else {
		dp[b][1] = f[b][1];
		dp[b][0] = INF;
	}
}

ll LCAans(int x,int y){
	int t;
	if(deep[x] < deep[y]) swap(x,y);
	while (deep[x] > deep[y] && father[x] != y){
		t = father[x];
		dp[t][1] = f[t][1] - min(f[x][0],f[x][1]) + min(dp[x][0],dp[x][1]);
		dp[t][0] = f[t][0] - f[x][1] + dp[x][1];
		x = t;
	}
	if(father[x] == y){
		dp[y][1] = dp[y][1] - min(f[x][0],f[x][1]) + min(dp[x][0],dp[x][1]);
		dp[y][0] = dp[y][0] - f[x][1] + dp[x][1];
		return min(dp[y][1] + ff[y][1],dp[y][0] + ff[y][0]);
	}

	while(father[x] != father[y]){
		t = father[x];
		dp[t][1] = f[t][1] - min(f[x][0],f[x][1]) + min(dp[x][0],dp[x][1]);
		dp[t][0] = f[t][0] - f[x][1] + dp[x][1];
		x = t;
		t = father[y];
		dp[t][1] = f[t][1] - min(f[y][0],f[y][1]) + min(dp[y][0],dp[y][1]);
		dp[t][0] = f[t][0] - f[y][1] + dp[y][1]; 
		y = t;
	}
	t = father[x];
	dp[t][1] = f[t][1] - min(f[x][0],f[x][1]) + min(dp[x][0],dp[x][1])
					   - min(f[y][0],f[y][1]) + min(dp[y][0],dp[y][1]);
	dp[t][0] = f[t][0] - f[x][1] + dp[x][1] - f[y][1] + dp[y][1];

	return min(dp[t][0] + ff[t][0],dp[t][1] + ff[t][1]);
}

int main(){
	Read();
	dfs1(1,0);
	dfs2(1,0);
	for(int i = 1;i <= m;i++){
		trans(i);
		ll res = LCAans(rest[i].node1,rest[i].node2);
		if(res < INF) cout << res << endl;
		else cout << "-1" << endl;
	}
	return 0;
}
