    for (int i=2;i<=p;i++)
    {
        s=s*2%123456789;
        if (s==2) break;
        t++;
    }
    p=p%t;s=1;
    if (p==0) p=t;
    for (int i=1;i<=p;i++)
    s=s*2%123456789;
    cout<<s;
