#include <iostream>

using namespace std;

typedef long long ll;

const ll mod = 123456789;
ll k;

ll ksm(ll a, ll b) {
    ll ans = 1, base = a;
    while(b) {
        if(b & 1) {
            ans *= base;
            ans %= mod;
        }
        base *= base;
        base %= mod;
        b >>= 1;
    }
    return ans;
}

int main() {
    cin >> k;
    cout << ksm(2, k - 1) << endl;
    return 0;
}
```

