        while (hsh[pos]) {
            if (vis[cur][hsh[pos]] == S) {
                dp[cur][hsh[pos]] += sum;
                return;
            }
            pos = (pos + 1) % kcz;
        }
        hsh[pos] = ++cnt[cur], vis[cur][hsh[pos]] = S, dp[cur][hsh[pos]] = sum;
    }

