                    res S = vis[cur ^ 1][k], l = (S >> ((j - 1) << 1)) & 3, r = (S >> (j << 1)) & 3;
                    if (!mp[i][j]) {
                        if (!l && !r)insert(S, dp[cur ^ 1][k]);
                        continue;
                    }
                    if (!l && !r) {
                        if (!mp[i][j + 1] || !mp[i + 1][j])continue;
                        insert(S ^ (1 << ((j - 1) << 1)) ^ (2 << (j << 1)), dp[cur ^ 1][k]);
                    }
