                        if (mp[i][j + 1])insert(S ^ (l << ((j - 1) << 1)) ^ (l << (j << 1)), dp[cur ^ 1][k]);
                        if (mp[i + 1][j])insert(S, dp[cur ^ 1][k]);
                    }
                    if (!l && r) {
                        if (mp[i + 1][j])insert(S ^ (r << (j << 1)) ^ (r << ((j - 1) << 1)), dp[cur ^ 1][k]);
                        if (mp[i][j + 1])insert(S, dp[cur ^ 1][k]);
                    }
                    if (l == 1 && r == 1) {
                        res dt = 1;
                        for (res p = j + 1; p <= m; p++) {
                            res val = (S >> (p << 1)) & 3;
                            if (val == 1)dt++;
                            if (val == 2)dt--;
                            if (!dt) {
                                S ^= (2 << (p << 1)) ^ (1 << (p << 1));
                                break;
                            }
                        }
                        insert(S ^ (1 << ((j - 1) << 1)) ^ (1 << (j << 1)), dp[cur ^ 1][k]);
                    }
                    if (l == 2 && r == 2) {
                        res dt = 1;
                        for (res p = j - 2; ~p; p--) {
