#define bel(a)((a-1)/318+1)
template<int MAXN>
struct BitSet{
	static const int SIZE=MAXN/64+1;
	long long b[SIZE];
	inline void flip(int i){b[i>>6]^=(1uLL<<(i&63));}
	inline int test(int i){return b[i>>6]>>(i&63)&1;}
};
BitSet<100001>b;
BitSet<319>t;
int main(){
