			const int L=bel(l),R=bel(r);
			if(L==R)for(int i=l;i<=r;++i)b.flip(i);else{
				for(int i=L+1;i<R;++i)t.flip(i);
				for(int i=l;bel(i)==L;++i)b.flip(i);
				for(int i=r;bel(i)==R;--i)b.flip(i);
			}
		}else{
			int x;
			scanf("%d",&x);
			putchar(b.test(x)^t.test(bel(x))^'0');
			putchar('\n');
		}
	}
	return 0;
}
