void change(int l, int r)
{
    for (l += N - 1, r += N + 1; l ^ r ^ 1; l >>= 1, r >>= 1)//比较标准的区间修改 
    {
        if (~l & 1)
        {
            lazy[l ^ 1] = !lazy[l ^ 1];
