#include <iostream>
#include <cstdio>

const int MAXN = 1e5;

int n, m;
int bit[MAXN | 1];

inline int read() {
    register int x = 0;
    register char ch = getchar();
    while(!isdigit(ch)) ch = getchar();
    while(isdigit(ch)) {
        x = x * 10 + ch - '0';
        ch = getchar();
    }
    return x;
}

inline void modify(int x, int y) {
    for(x; x <= n; x += x & (-x)) {
        bit[x] += y;
    }
}

inline int query(int x) {
    int res = 0;
    while(x > 0) {
        res += bit[x];
        x -= x & (-x);
    }
    return res;
}

int main() {
    n = read();
    m = read();
    while(m--) {
        int opt = read();
        if(opt == 1) {
            int a = read();
            int b = read();
            modify(a, 1);
            modify(b + 1, -1);
        } else printf("%d\n", query(read()) & 1);
    }
    return 0;
}
```

