#include <bits/stdc++.h>
using namespace std;
int T,n,m,a[15][15];
const int MAX = 1 << 13; //m+1位
long long dp[2][MAX];
inline int query(const int &val,const int &pos) {return val & (1 << (pos - 1));} //查询val的第pos位
inline void ch(int &val,const int &pos,const int &vv) 
{val &= ~(1 << (pos - 1));val |= (vv << (pos - 1));} //把val的第pos位改成vv
long long work()
{
