inline void calrev()
{
	int lg=log(Len)/log(2)-1;
	Rep(i,1,Len-1)rev[i]=rev[i>>1]>>1|(i&1)<<lg;
}

static int T[MAXN];

inline void Inv(int*a,int*b,int n)
{
	if(n==1){b[0]=1;return;}
	Inv(a,b,n>>1);
	Len=n<<1;
	Rep(i,0,n)T[i]=a[i];
	calrev();
	mul(T,b),mul(T,b);
	Rep(i,0,n-1)b[i]=(2*b[i]-T[i]+mod)%mod;
}

inline void solve()
{
