	while(q--)
	{
		int x,y;
		scanf("%d%d",&x,&y);
		addedge(x,y+n);
		addedge(y+n,x);
	}
	for(int i=1;i<=n+m;i++)if(!dfn[i])tarjan(i);
	printf("%d",tot-1);
    return 0;
