long long readint(long long j) {
    long long frog = 1, x = 0;char c = getchar();
    while(c < '0' || c > '9' ) {
        if(c == '-')
            frog = -1;
        c = getchar();
    }
    while(c >= '0' && c <= '9'){
        x = ((x << 3) + (x << 1) + c - '0') % j;
        c = getchar();
    }
    return x * frog == 0 ? j : x * frog;
}

long long power(long long a, long long b, long long j) {
    long long ans = 1, base = a % j;
    while(b) {
        if(b & 1)
            ans = ans * base % j;
        base = base * base % j;
        b >>= 1;
    }
    return ans;
}

