inline int read(){
    register int u = 0,f=1;
    while( * s < 48 ) if(*s++=='-') f=-1;
    while( * s >=48 ) u = u * 10 + * s++ - 48;
    return u*f;
}

inline int calc(int x){
    int t=x%4;
    if(t==0) return (x<<1);
    else if(t==1) return 3;
    else if(t==2) return ((x+1)<<1);
    else return 1;
}//1-x的异或和，打表打出来的
const int N=2*1e7+5;
int rt,cnt;
int ls[N],rs[N],mx[N],mn[N],tag[N];ll sum[N];
#define ls ls[rt]
#define rs rs[rt]

inline void update(int &rt,int l,int r,int L,int R){
    if(tag[rt]) return;
    if(!rt) rt=++cnt;
    if(L<=l&&r<=R){
        mn[rt]=l,mx[rt]=r;
        sum[rt]=calc(r-1)^calc(l-1);
        tag[rt]=1;
        return;
    }
    int mid=(l+r)>>1;
    if(L<=mid) update(ls,l,mid,L,R);
    if(R>mid) update(rs,mid+1,r,L,R);
