	cin >> n >> k;
	for (int i = 1; i <= n; ++i)scanf("%d", &a[i]);
	F[0] = G[0] = 1; now = 1;
	for (int i = 1; i <= n; ++i)
	{
		for (int j = k; j >= 1; --j)
		{
			f[j] = g[j] = 0;
			for (int o = 0; o < j; ++o)(f[j] += F[o]) %= mod;
			(F[j] += f[j]) %= mod;
			for (int o = 0; o < j; ++o)(g[j] += G[o] * ksm(a[i], j - o, mod)) %= mod;
			(G[j] += g[j]) %= mod;
		}
